mapper.select:
select
  id, user_id, default_status, receive_name, phone, province, city, region, detail_address, create_time
from address


        and id = #{id}


        and user_id = #{userId}


        and default_status = #{defaultStatus}


        and receive_name = #{receiveName}


        and phone = #{phone}


        and province = #{province}


        and city = #{city}


        and region = #{region}


        and detail_address = #{detailAddress}


        and create_time = #{createTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from address


        and id = #{id}


        and user_id = #{userId}


        and default_status = #{defaultStatus}


        and receive_name = #{receiveName}


        and phone = #{phone}


        and province = #{province}


        and city = #{city}


        and region = #{region}


        and detail_address = #{detailAddress}


        and create_time = #{createTime}

select *
from address


        and id = #{id}


        and user_id = #{userId}


        and default_status = #{defaultStatus}


        and receive_name = #{receiveName}


        and phone = #{phone}


        and province = #{province}


        and city = #{city}


        and region = #{region}


        and detail_address = #{detailAddress}


        and create_time = #{createTime}

mapper.update:
update address
set default_status=0
where user_id=#{userId};
